import React from 'react'

import './NewExpense.css';
import ExpenseForm from './ExpenseForm.js';
function NewExpense(props) {
  const saveExpenseDataHandler = (enteredExpenseData)=>{
    const expenseData = {
      ...enteredExpenseData,
      id:Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  }

  let isFormVisible = true;

  const setFormVisible =()=>{
   isFormVisible = !isFormVisible;
  } 
  //change boolean of isFormvisible

  let NoformContent = <div className='new-expense__Close'><button onClick={setFormVisible}>Add New Expense</button></div>
  //default value

  if(!isFormVisible){ 
    NoformContent = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler}></ExpenseForm>
  }
  //Form Open Conditional Statement

  return (
    <div className='new-expense'>
        {NoformContent}
    </div>
  )
}

export default NewExpense
