import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [moives, setMovies] = useState([]);
  const [isLoading, setIsLoding] = useState(false);
  const [error,setError] = useState(null);

  async function fetchMoviesHandler() {
    setIsLoding(true);
    setError(null);

    try{
      const response = await fetch("https://swapi.dev/api/film/");
      if(!response.ok){
        throw new Error('somethin went wrong');
      }
      const data = await response.json();

      
      // @ response.ok => 응답이 잘 왔는지 확인하는것
      // throw new Error => 예외를 발생시키는것, 예외가 발생하면 함수가 중지되고 catch문으로 넘어감
      // response.ok가 false(무언가 문제가 발생)이면 throw로 예외를 발생시키고 catch문으로 전달한다.
      const transformedMovies = data.results.map((movieData) => {
          return {
            id: movieData.episode_id,
            title: movieData.title,
            openingText: movieData.opening_crawl,
            releaseDate: movieData.release_date,
          };
        });
      setMovies(transformedMovies);
      setIsLoding(false);
    } catch(error){
      setError(error.message);
      setIsLoding(false); // 오류가 발생하면 로딩은 항상 중단
    }
    
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading &&moives.length>0 && <MoviesList movies={moives} />}
        {!isLoading&&moives.length===0&&!error &&<p>found no movies.</p>}
        {!isLoading && error&&<p>{error}</p>}
        {isLoading && <p>Loading....</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
